import User from '../components/User';
import fetch from 'isomorphic-unfetch';

const Index = ({ users }) => (
  <ul>
    {users.map((u, i) => (
      <User key={i} user={u} />
    ))}
  </ul>
);

Index.getInitialProps = async () => {
  const res = await fetch('http://localhost:3000/api/users');
  const users = await res.json();

  return { users }
}

export default Index
